package main

import "fmt"

func main() {
	// s1, s2 := "ab", "eidboaooo"
	s1, s2 := "adc", "dcda"
	// s1, s2 := "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz",
	// 	""
	// s1, s2 := "hello", "ooolleoooleh"
	// s1, s2 := "pqzhi", "ghrqpihzybre"
	fmt.Println("Output: ", checkInclusion(s1, s2))
}

func checkInclusion(s1 string, s2 string) bool {
	dic, buf := make([]int, 26), make([]int, 26)
	for i := range s1 {
		dic[s1[i]-'a']++
	}

	i, j := 0, 0
	for i < len(s2) {
		if dic[s2[i]-'a'] > 0 {
			if i+len(s1) > len(s2) {
				break
			}
			for j = i + 1; j < i+len(s1); j++ {
				if dic[s2[j]-'a'] == 0 {
					i = j
					break
				}
			}
			if j == i+len(s1) {
				copy(buf, dic)
				if isPermutation(s2[i:j], buf) {
					return true
				}
			}
		}
		i++
	}

	return false
}

func isPermutation(s string, dic []int) bool {
	cnt := 0
	for i := range s {
		if dic[s[i]-'a'] > 0 {
			dic[s[i]-'a']--
			cnt++
		} else {
			return false
		}
	}
	return cnt == len(s)
}

func checkInclusionV0(s1 string, s2 string) bool {
	dic := make([]int, 26)
	for i := range s1 {
		dic[s1[i]-'a']++
	}

	buf := make([]int, 26)
	copy(buf, dic)
	cnt := 0
	for i := range s2 {
		if buf[s2[i]-'a'] > 0 {
			buf[s2[i]-'a']--
			cnt++
			if cnt == len(s1) {
				return true
			}
		} else if cnt > 0 {
			buf = make([]int, 26)
			copy(buf, dic)
			cnt = 0
		}
	}

	buf = make([]int, 26)
	copy(buf, dic)
	cnt = 0
	for i := len(s2) - 1; i >= 0; i-- {
		if buf[s2[i]-'a'] > 0 {
			buf[s2[i]-'a']--
			cnt++
			if cnt == len(s1) {
				return true
			}
		} else if cnt > 0 {
			buf = make([]int, 26)
			copy(buf, dic)
			cnt = 0
		}
	}
	return false
}
